
public class FloatingLiteral {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(10.66f);//4-bytes
		System.out.println(13.567f);//float literals
		System.out.println(12.67);//8-bytes
		System.out.println(13.45d);//double float
		System.out.println(0.6);
		System.out.println(145.0);
		System.out.println(1.0E6);//1*10^3
		System.out.println(1.0E7);
		System.out.println(1.0E-3);
	}

}
